INSERT INTO ims_document_chapter_content (id, chapter_id, content,layout) VALUES(  '1006', '1702', '1：这里说明下会员注册的方式，有很多用户不明白会员的注册方式，系统自动注册，是粉丝关注公众号后，自动注册为会员，会员手动注册，需要会员在某个功能页面内，登录后，填写信息注册会员
