INSERT INTO ims_document_chapter_content (id, chapter_id, content,layout) VALUES(  '1671', '2799', '发票是记录经营活动的一种原始证明。由于发票上载明的经济事项较为完整，既有填制单位印章，又有经办人签章，还有监制机关、字轨号码、发票代码等，具有法律证明效力。它为工商部门检查经济合同，处理合同纠纷，法院裁定民事诉讼，消费者向销货方要
