namespace W7\\App\\Channel\\Session;

use W7\\App;
use W7\\Core\\Session\\Channel\\ChannelAbstract;
use W7\\Http\\Message\\Server\\Response;

class TestChannel extends ChannelAbstract {
	public function getSessionId() {
		if (!$this->sessionId) {
			$sessionId = $this->request->getHeaderLine(ucfirst(strtolower($this->getSessionName())));
			if (!$sessionId) {
				$sessionId = $this->generateId();
			}
			$this->sessionId = $sessionId;
		}


		return $this->sessionId;
	}

	public function replenishResponse(Response $response): Response {
		$response = $response->withHeader($this->getSessionName(), $this->getSessionId());
		App::getApp()->getContext()->setResponse($response);
		return $response;
	}
}
```
